import React from "react"
import { Component } from "react"

class App extends Component{

    constructor() {
        super()
        this.state = {
            isLoggedIn: false
        }
    }

    render(){
        let stateLogged

        if (this.state.isLoggedIn) {
            stateLogged = "OUT"
        } else {
            stateLogged = "IN"
        }

        return(
            <div>
                <h1>You are currently logged {stateLogged}</h1>
            </div>
        )
    }
}

export default App
