import React, { Component } from 'react'

class App extends Component {
    constructor(){
        super()
        this.state = {
            isLogged: true
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange() {
        this.setState(prevState => {
            this.state.isLogged != isLogged
        })

        return {
            isLogged
        }
    }

    render (){
        return (
            <div>
                {
                    this.state.isLogged === true ? <button>WYLOGUJ</button> : <button>ZALOGUJ</button>
                }
            </div>
        )
    }
}

export default App
