import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        this.setState({
            [firstName]: event.target.name
        })
    }

    render() {
        return (
            <main>
                <form onSubmit={this.handleSubmit}>
                    <input placeholder="First Name" name="firstName" onChange={this.handleChange} /> <br />
                    <input placeholder="Last Name" name="lastName" /> <br />
                    <input placeholder="Age" name="age" /> <br />
                    <label>
                        <input
                            type="radio"
                            name="genderMale"
                            checked={this.state.isFriendly}
                            onChange={this.handleChange}
                        /> Male
                    </label>

                    <label>
                        <input
                            type="radio"
                            name="generFemale"
                            checked={this.state.isFriendly}
                            onChange={this.handleChange}
                        /> Female
                    </label>

                    <br />
                    <button>Submit</button>
                </form>
                <h1>First name: {this.state.firstName}</h1>
            </main>
        )
    }
}

export default App
