import React from 'react'

class App extends React.Component {
    constructor(){
        super()
        this.state = {
            isLogged: false
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange() {
        this.setState(prevState => {
            return {
                isLogged: !prevState.isLogged
            }
        })
    }

    render (){
        return (
            <div>
                <h1>Jeste≈õ obecnie, </h1>{this.state.isLogged === true ? <h1>zalogowany - Witaj Sebastian</h1> : <h1>wylogowany</h1>}
                {this.state.isLogged === true ? <button onClick={this.handleChange}>WYLOGUJ</button> : <button onClick={this.handleChange}>ZALOGUJ</button>}
            </div>
        )
    }
}

export default App
