import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            age: "",
            gender: "",
            destination: "",
            isVegan: false,
            isKosher: false,
            isLactoseFree: false
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name] : checked }) : this.setState({ [name]: value })
        // type === "checkbox" ?
        //     this.setState({
        //         diataryRestrictions: {
        //             [name]: checked
        //         }
        //     }) :
        //     this.setState({
        //         diataryRestrictions: {
        //             [name]: value
        //         }
        //     })
    }

    render() {
        return (
            <main>
                <form onSubmit={this.handleSubmit}>
                    <input
                        placeholder="First Name" 
                        value={this.state.firstName}
                        name="firstName"
                        onChange={this.handleChange}
                    />

                    <br /><br />

                    <input
                        placeholder="Last Name"
                        value={this.state.lastName}
                        name="lastName"
                        onChange={this.handleChange}
                    />

                    <br /><br />

                    <input
                        placeholder="Age"
                        value={this.state.age}
                        name="age"
                        onChange={this.handleChange}
                    />

                    <br /><br />

                    <label>
                        <input
                            type="radio"
                            name="gender"
                            value="male"
                            checked={this.state.gender === "male"}
                            onChange={this.handleChange}
                        /> Male
                    </label>

                    <label>
                        <input
                            type="radio"
                            name="gender"
                            value="female"
                            checked={this.state.gender === "female"}
                            onChange={this.handleChange}
                        /> Female
                    </label>

                    <br /><br />

                    <label>Destination </label> 
                    <select
                        value = {this.state.destination}
                        onChange = {this.handleChange}
                        name = "destination"
                    >
                    <option value="">--- Please choose destination ---</option>
                    <option value="Wieden">Wieden</option>
                    <option value="Kuba">Kuba</option>
                    <option value="Meksyk">Meksyk</option>
                    <option value="USA">USA</option>
                    <option value="Londyn">Londyn</option>
                    </select>

                    <br /><br />

                    <label>
                        <input
                            type="checkbox"
                            name="isVegan"
                            onChange={this.handleChange}
                            checked={this.state.isVegan}
                        /> Vegan
                    </label>

                    <label>
                        <input
                            type="checkbox"
                            name="isKosher"
                            onChange={this.handleChange}
                            checked={this.state.isKosher}
                        /> Kosher
                    </label>

                    <label>
                        <input
                            type="checkbox"
                            name="isLactoseFree"
                            onChange={this.handleChange}
                            checked={this.state.isLactoseFree}
                        /> Lactose Free
                    </label>

                    <br /><br />

                    <button>Submit</button>
                </form>

                <hr />

                <h3>First name: {this.state.firstName}</h3>
                <h3>Last name: {this.state.lastName}</h3>
                <h3>Age: {this.state.age}</h3>
                <h3>Gender: {this.state.gender}</h3>
                <h3>Destination: {this.state.destination}</h3>
                <h3>Dietary restrictions:</h3>
                <p>Vegan: {this.state.isVegan ? "Yes" : "No"}</p>
                <p>Kosher: {this.state.isKosher ? "Yes" : "No"}</p>
                <p>Lactose Free: {this.state.isLactoseFree ? "Yes" : "No"}</p>
            </main>
        )
    }
}

export default App
