import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            age: "",
            gender: "",
            destination: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        const {name, value, type, checked} = event.target
        this.setState({
            [name]: value
        })
    }

    render() {
        return (
            <main>
                <form onSubmit={this.handleSubmit}>
                    <input
                        placeholder="First Name" 
                        value={this.state.firstName}
                        name="firstName"
                        onChange={this.handleChange}
                    /> <br />
                    <input
                        placeholder="Last Name"
                        value={this.state.lastName}
                        name="lastName"
                        onChange={this.handleChange}
                    /> <br />
                    <input
                        placeholder="Age"
                        value={this.state.age}
                        name="age"
                        onChange={this.handleChange}
                    /> <br />
                    <label>
                        <input
                            type="radio"
                            name="gender"
                            value="male"
                            checked={this.state.gender === "male"}
                            onChange={this.handleChange}
                        /> Male
                    </label>

                    <label>
                        <input
                            type="radio"
                            name="gender"
                            value="female"
                            checked={this.state.gender === "female"}
                            onChange={this.handleChange}
                        /> Female
                    </label>

                    <br /><br />

                    <label>Destination </label> 
                    <select
                        value = {this.state.destination}
                        onChange = {this.state.onChange}
                        name = "destination"
                    >
                    <option value="wieden">Wieden</option>
                    <option value="kuba">Kuba</option>
                    <option value="meksyk">Meksyk</option>
                    <option value="usa">USA</option>
                    <option value="londyn">Londyn</option>
                    </select>

                    <br />
                    <button>Submit</button>
                </form>

                <hr />

                <h3>First name: {this.state.firstName}</h3>
                <h3>Last name: {this.state.lastName}</h3>
                <h3>Age: {this.state.age}</h3>
                <h3>Gender: {this.state.gender}</h3>
                <h3>Destination: {this.state.destination}</h3>
            </main>
        )
    }
}

export default App
