import React from "react"
import { Component } from "react"

class App extends Component{

    constructor() {
        super()
        this.logged = {
            login: false
        }
    }

    render(){

        let state

        if (this.logged.login == false) {
            state = "IN"
        } else {
            state = "OUT"
        }

        return(
            <div>
                <h1>You are currently logged (in/out)</h1>
            </div>
        )
    }
}

export default App



if (hours < 12) {
