import React from 'react'

class App extends React.Component {
    constructor(){
        super()
        this.state = {
            isLogged: false
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange() {
        this.setState(prevState => {
            return {
                isLogged: !prevState.isLogged
            }
        })
    }

    render () {
        let buttonText = this.state.isLogged ? "WYLOGUJ" : "ZALOGUJ"
        let displayText = this.state.isLogged ? "zalogowany" : "wylogowany"

        return (
            <div>
                <h1>{displayText}</h1>
                <button onClick={this.handleChange}>{buttonText}</button>
            </div>
        )
    }
}

export default App
