import React from "react"
import "./style.css"

function Joke(props) {
  return(
    <div className="section">
      <div className="joke">
          <p style={{display: props.joke.question ? "block" : "none"}}><strong>Zagadka:</strong> {props.joke.question}</p>
          <p style={{color: !props.joke.question && "pink" }}><strong>Odpowied≈∫:</strong> {props.joke.punchline}</p>
      </div>
    </div>
  )
}

export default Joke
